import CleanTechElement from './CleanTechElement';

import '../Assets/Styles/Content.css';

import { useState, useEffect } from "react";

function Content() {
    const [sourcesMetrics, setSourcesMetrics] = useState({});
    const [proxyFetchError, setProxyFetchError] = useState(false);

    /*useEffect(() => {
        makeRequest();
    }, []);

    useEffect(() => {
        makeRequest();
        const fetchTimeout = setTimeout(() => {
            makeRequest();
        }, 2000);

        return () => {
            clearTimeout(fetchTimeout);
        };
    });*/

    const makeRequest = () => {
        fetch('http://localhost:5281/CleanTechProxy')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Request failed');
                }
                return response.json();
            })
            .then(json => {
                setProxyFetchError(false);
                setSourcesMetrics(json);
            })
            .catch(error => {
                setProxyFetchError(true);
            })
            .finally(() => {
                // Schedule the next request after the fetchInterval
                setTimeout(makeRequest, 2000);
            });
    };

    makeRequest();

    const cleanTechDataRequest = () => {
        fetch('http://localhost:5281/CleanTechProxy')
        .then(response => response.json())
        .then(json => {
            setSourcesMetrics(json);
            setProxyFetchError(false);
        })
        .catch(() => {
            setProxyFetchError(true);
        })
    }

    const proxyErrorMessage = proxyFetchError ? (
        <div>proxy error message</div>
    ) : (
        ''
    );
    
    return(
        <div className='content'>
            {proxyErrorMessage}
            {Object.keys(sourcesMetrics).length == 0 ? (
                <div className='loadingScreen' />
            ) : (
                <div className='content_elements'>
                    <CleanTechElement data={sourcesMetrics.windmillData} />
                    <CleanTechElement data={sourcesMetrics.solarPanelData} />
                    
                </div>
            )}
        </div>
    );
}

export default Content;
