import CleanTechElement from './CleanTechElement';

import '../Assets/Styles/Content.css';

import { useState, useEffect } from "react";

function Content() {
    const [sourcesMetrics, setSourcesMetrics] = useState({});
    const [proxyFetchError, setProxyFetchError] = useState(false);

    useEffect(() => {
        console.log("beforeRequest");
        cleanTechDataRequest();
        console.log("afterRequest");
    }, []);

    useEffect(() => {
        const fetchTimeout = setTimeout(() => {
            cleanTechDataRequest()
        }, 2000);

        return () => {
            clearTimeout(fetchTimeout);
        };
    });

    const cleanTechDataRequest = () => {
        fetch('http://localhost:5281/CleanTechProxy')
        .then(response => response.json())
        .then(json => setSourcesMetrics(json))
        .catch(() => setProxyFetchError(true));

        if (proxyFetchError) setProxyFetchError(false);
    }

    console.log('render');
    console.log(sourcesMetrics);
    //console.log("epmty response: " + Object.keys(sourcesMetrics).length == 0);
    //console.log("contains error: " + Object.values(sourcesMetrics).includes('error'));


    //const waitingForResponse = Object.keys(sourcesMetrics).length == 0 || Object.values(sourcesMetrics).includes('error');

    
    return(
        <div className='dashboard__content'>
            
            {Object.keys(sourcesMetrics).length == 0 ? (
                <div>waiting for response</div>
            ) : (
                <>
                    <CleanTechElement data={sourcesMetrics.windmillData} />
                    <CleanTechElement data={sourcesMetrics.solarPanelData} />
                </>
            )}
        </div>
    );
}

export default Content;
